import { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import InputInfo from './InputInfo';
import './UserInputForm.scss';

const UserInputForm = () => {
    const [info, setInfo] = useState({
        nickname: '',
        email: '',
        password: '',
        isValidNickname: null,
        isValidPwd: null,
        isValidPwdChk: null,
    });

    const [isValidButton, setIsValidButton] = useState(false);
    const navigate = useNavigate();

    const infoHandler = (key, value) => {
        setInfo(prevState => ({
                ...prevState,
                [key]: value 
        }));
    };

    const validationHandler = useCallback((value) => {
        setInfo(prevState => ({
            ...prevState,
            isValid: value,
        }));
    }, []);

    const submitHandler = event => {
        event.preventDefault();
    }

    useEffect(() => {
        setIsValidButton(info.isValid);
    }, [info.isValid]);

    return (
        <form className="regsiterSubmitForm" onSubmit={submitHandler}>
            <InputInfo onChange={infoHandler} OnChangeValidation={validationHandler} />
            <button disabled={!isValidButton}>회원가입</button>
        </form>
    );
};

export default UserInputForm;
