import { useState, useEffect } from "react";
import './InputInfo.scss';

const InputInfo = ({ onChange, OnChangeValidation }) => {
    const [inpuData, setInputData] = useState({
        nickname: "",
        email: "",
        password: "",
        passwordCheck: "",
    });

    const [isValid, setIsValid] = useState({
        nickname: false,
        email: true,
        password: false,
        passwordCheck: false,
    });

    const inputHandler = (event) => {
        const { name, value } = event.target;

        setInputData((prev) => ({ ...prev, [name]: value }));

        if (name === "nickname") {
            setIsValid((prev) => ({ ...prev, nickname: value.length >= 4 && value.length <= 12 }));
        }
        if (name === "email") {
            const isValidEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value);
            setIsValid((prev) => ({ ...prev, email: isValidEmail }));
        }
        if (name === "password") {
            const isValidPassword = /^(?=.*[a-zA-Z])(?=.*\d)(?=.*[!@#$%^&*]).{8,16}$/.test(value);
            setIsValid((prev) => ({
                ...prev,
                password: isValidPassword,
                passwordCheck: inpuData.passwordCheck === value,
            }));
        }
        if (name === "passwordCheck") {
            setIsValid((prev) => ({ ...prev, passwordCheck: inpuData.password === value }));
        }
        onChange(name, value);
    };

    useEffect(() => {
        OnChangeValidation("userInfo", Object.values(isValid).every(Boolean));
    }, [isValid, OnChangeValidation]);

    return (
        <>
            <input {...INPUT_FIELDS[0]} value={inputData.nickname} onChange={inputHandler} />
            {isValid.nickname && <p className="valid">사용 가능한 닉네임입니다.</p>}
            {!isValid.nickname && inpuData.nickname && (
                <p className="invalid">닉네임은 4~12자 영문과 숫자로 입력해 주세요.</p>
                )}

            <input {...INPUT_FIELDS[1]} value={inputData.email} onChange={inputHandler} />
            {isValid.email ? (
                <p className="valid">올바른 이메일 형식입니다.</p>
            ): (
                <p className="invalid">올바른 이메일 주소를 입력해 주세요.</p>
            )}

            <input {...INPUT_FIELDS[2]} value={inputData.password} onChange={inputHandler} />
            {isValid.password && <p className="valid">사용 가능한 비밀번호입니다.</p>}
            {!isValid.password && inputData.password && (
                <p className="invalid">비밀번호는 영문, 숫자, 특수문자를 포함해 8~16자로 입력해 주세요.</p>
            )}

            <input {...INPUT_FIELDS[3]} value={inputData.passwordCheck} onChange={inputHandler} />
            {isValid.passwordCheck && <p className="valid">비밀번호가 일치합니다.</p>}
            {!isValid.passwordCheck && inputData.passwordCheck && (
                <p className="invalid">비밀번호를 한 번 더 입력해 주세요.</p>
            )}
        </>
    );
};

const INPUT_FIELDS = [
    { id: 1, name: "nickname", placeholer: "닉네임", type: "text" },
    { id: 2, name: "email", placeholer: "이메일", type: "text" },
    { id: 3, name: "password", placeholer: "비밀번호", type: "password" },
    { id: 4, name: "passwordCheck", placeholer: "비밀번호 확인", type: "password" },
];

export default InputInfo;
