import { useState, useEffect } from "react";
import './InputPersonalInfo.scss';

const InputPersonalInfo = ({ onchange, onChangeValidation }) => {
    const [isValidInputNickname, setIsValidInputNickname] = useState(false);

    const InputHandler = event => {
        const { nickname, value } = event.target;

        if (nickname === 'username') {
            if (value.length === 0) {
                alert('닉네임을 입력해주세요.');
                setIsValidInputNickname(false);
                onchange(nickname, '');
            }
            else {
                setIsValidInputNickname(true);
                onchange(nickname, value);
            }
        }
        if (nickname === 'email') {
            onchange(nickname, value);
        }
    };

    useEffect(() => {
        onChangeValidation(
            'personalInfo',
            isValidInputNickname
        );
    }, [
        onChangeValidation,
        isValidInputNickname,
    ]);

    return (
        <>
            <input {...PERSONAL_INFO[0]} onChange={InputHandler} />
            <input
                {... PERSONAL_INFO[1]}
                onChange={inputHandler}
            />
            <input {...PERSONAL_INFO[3]} onChange={InputHandler} />
        </>
    );
};

const PERSONAL_INFO = [
    {
        id: 1, 
        name: 'nickname',
        placeholder: '닉네임', 
        type: 'text' 
    },
    {
        id: 2,
        name: 'email',
        placeholder: '이메일',
        type: 'text',
    },
];

export default InputPersonalInfo;
